    @Test
    public void testEight() {
        String code = "3,21,1008,21,8,20,1005,20,22,107,8,21,20,1006,20,31,1106,0,36,98,0,0,1002,21,125,20,4,20,1105,1,46,104,999,1105,1,46,1101,1000,1,20,4,20,1105,1,46,98,99";
        IntCode prog = new IntCode(0, code);
        assertEquals(Arrays.asList(999), prog.run(IntCode.inPipe(new int[]{7}), null));
        prog.reset();
        assertEquals(Arrays.asList(1000), prog.run(IntCode.inPipe(new int[]{8}), null));
        prog.reset();
        assertEquals(Arrays.asList(1001), prog.run(IntCode.inPipe(new int[]{10}), null));
    }

